import React from "react";

function WeatherAPI(cityName, units) {
  
  
  const API_LINK = "https://api.openweathermap.org/data/2.5/forecast?q=";
  const API_CITY = cityName || "Gdańsk";
  const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";
  const API_UNITS =
    units.checked === false ? "&units=metric" : "&units=imperial";
  const API_LANG = "&lang=pl";
  const API_CROSS = "https://cors-anywhere.herokuapp.com/";

  const API_URL = API_CROSS + API_LINK + API_CITY + API_UNITS + API_KEY;

  fetch(API_URL, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((res) => {
      if (res.ok) {
        console.log(res);
      } else {
        console.log("Mamy problem !");
      }
    })
    .catch((error) => {
      console.error(error);
    });
}

export default WeatherAPI;
