import logo from "../img/logo2.png";
import sun from "../img/weather-icons/sun.png";
import "../App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import WeatherAPI from "../scripts/WeatherAPI";
import Layout from "../layout/layout";
import WeatherAPIConf from "../scripts/WeatherAPIConf";
import iconThunderstorm from "../img/weather-icons/thunderstorm.png";
import iconDizzle from "../img/weather-icons/drizzle.png";
import iconRain from "../img/weather-icons/rain.png";
import iconIce from "../img/weather-icons/ice.png";
import iconUnknown from "../img/weather-icons/unknown.png";
import iconFog from "../img/weather-icons/fog.png";
import iconSun from "../img/weather-icons/sun.png";
import iconCloud from "../img/weather-icons/cloud.png";

function Main({ submitHandler }) {
  const [cityName, setCityName] = useState("");
  const [units, setUnits] = useState(false);
  const [temperature, setTemperature] = useState(null);
  const [status, setStatus] = useState(null);
  const [exampleCityName, setExampleCityName] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(units);
    //
    //console.log(WeatherAPI(cityName, units));
    submitHandler({ cityName, units });
    navigate("/weatherforcast");
  };

  // const searchCityWeather = (tab) => {
  //   //wykonuje zapytanie do api w funkcji
  //   for (let i = 0; i < tab.lenght; i++) {
  //     return <div>{WeatherAPI(tab[i], units)}</div>;
  //   }
  // };

  let getWeather = (city, units = false) => {
    WeatherAPIConf.API_FORMAT = "weather";
    WeatherAPIConf.API_CITY = city;
    WeatherAPIConf.API_UNITS =
      units === false ? "&units=metric" : "&units=imperial";

    
    const API_URL =
      WeatherAPIConf.API_LINK +
      WeatherAPIConf.API_FORMAT +
      WeatherAPIConf.API_CITYQ +
      WeatherAPIConf.API_CITY +
      WeatherAPIConf.API_UNITS +
      WeatherAPIConf.API_KEY;

    let tabWeek = [];
    //console.log(API_URL);
    axios
      .get(API_URL)
      .then((res) => {
        let temperature = res.data.main["temp"];
        console.log()
        let status = res.data.weather[0]["id"];
        tabWeek.push([temperature, status]);
        setTemperature(Math.round(tabWeek[0]));
        setStatus(tabWeek[1]);
        setExampleCityName(city);
      })
      .catch((error) => {
        //console.error("Błąd pobierania prognozy pogody:", error);
      });
  };

  const getIcon = (statusCode) => {
    if (statusCode >= 200 && statusCode < 300) {
      return iconThunderstorm;
    } else if (statusCode >= 300 && statusCode < 400) {
      return iconDizzle;
    } else if (statusCode >= 500 && statusCode < 600) {
      return iconRain;
    } else if (statusCode >= 600 && statusCode < 700) {
      return iconIce;
    } else if (statusCode >= 700 && statusCode < 800) {
      return iconFog;
    } else if (statusCode == 800) {
      return iconSun;
    } else if (statusCode > 800 && statusCode < 900) {
      return iconCloud;
    }
  };

  let exampleCityWeather = (tab) => {
    
    let tempUnit = units === false ? "°C" : "°F";
    //console.log(tab1.length);
    for (let i = 0; i < tab.length; i++) {
      //console.log(tab[i]);
      

      
    }
    getWeather("Warszawa", units);

    return (
      <div id="exampleCity">
        <div className="App-panel2">
          <h3>{exampleCityName}</h3>
          <div className="App-degree">
            <p>{console.log(temperature)}{units}</p>
          </div>
          <div>
            <img src={getIcon(status)} alt="sun" className="App-icon" />
          </div>
        </div>
      </div>
    );
    console.log("XD");
  };

  return (
    <Layout>
      <section>
        <div id="searchPanel">
          <div className="App-panel">
            <h2>Search the wather</h2>
            <form method="GET" onSubmit={handleSubmit}>
              <div class="button r" id="button-3">
                <input
                  type="checkbox"
                  class="checkbox units"
                  name="units"
                  checked={units}
                  onChange={(event) => setUnits(event.target.checked)}
                />
                <div class="knobs"></div>
                <div class="layer"></div>
              </div>
              <input
                type="text"
                name="city"
                id="city"
                placeholder="city"
                value={cityName}
                onChange={(event) => setCityName(event.target.value)}
              />
              <input type="submit" value="Search" />
            </form>
          </div>
        </div>
        {exampleCityWeather(["Warszawa", "Nowy York", "Londyn", "Paryż"], units)}
      </section>
    </Layout>
  );
}

export default Main;
