import React from "react";
import Axios from "axios";
import Chart from "chart.js";



function WeatherAPI(cityName, units) {
  const [hourlyData, setHourlyData] = useState(null);

  const API_LINK = "https://api.openweathermap.org/data/2.5/weather?q=";
  const API_CITY = cityName || "Gdańsk";
  const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";
  const API_UNITS = units === false ? "&units=metric" : "&units=imperial";
  const API_LANG = "&lang=pl";

  const API_URL = API_LINK + API_CITY + API_UNITS + API_KEY;

  useEffect(() => {
    async function fetchWeatherData() {
      try {
        const response = await axios.get(
          `${API_URL}?key=${API_KEY}&q=YOUR_CITY&days=1&aqi=no&alerts=no&hour=24`
        );
        const data = response.data;

        // Przetwarzanie danych godzinowej prognozy pogody
        const hourlyForecast = data.forecast.forecastday[0].hour.map(
          (item) => ({
            time: item.time, // Czas
            temperature: item.temp_c, // Temperatura w stopniach Celsiusza
            // Dodaj inne niezbędne informacje
          })
        );

        setHourlyData(hourlyForecast);
      } catch (error) {
        console.error("Błąd pobierania prognozy pogody:", error);
      }
    }

    fetchWeatherData();
  }, []);

  useEffect(() => {
    if (hourlyData) {
      // Generowanie wykresu po otrzymaniu danych godzinowej prognozy pogody
      generateChart();
    }
  }, [hourlyData]);

  const generateChart = () => {
    const labels = hourlyData.map((item) => item.time);
    const temperatures = hourlyData.map((item) => item.temperature);

    const ctx = document.getElementById("weatherChart").getContext("2d");
    new Chart(ctx, {
      type: "line",
      data: {
        labels: labels,
        datasets: [
          {
            label: "Temperatura (°C)",
            data: temperatures,
            borderColor: "blue",
            backgroundColor: "rgba(0, 0, 255, 0.2)",
            fill: true,
          },
        ],
      },
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  return <canvas id="weatherChart" width="400" height="200" />;
}

export default WeatherAPI;
