import React from "react";
import axios from "axios";

function WeatherAPI(cityName, units) {
  const API_LINK = "https://api.openweathermap.org/data/2.5/forecast?q=";
  const API_CITY =  "Gdańsk";
  const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";
  const API_UNITS = units === false ? "&units=metric" : "&units=imperial";
  const API_LANG = "&lang=pl";

  const API_URL = API_LINK + API_CITY + API_UNITS + API_KEY;

  axios
    .get(API_URL)
    .then((res) => {
      let morning = 2;
      let afternoon = 4;
      let evening = 6;
      let night = 7;
      let dayTab = [afternoon, night];
      console.log(res.data);
      
        for (let j = 0; j <= 32; j += 8) {
          for (let i = 0; i < dayTab.length; i+=2) {
          console.log(j);
          console.log(res.data.list[dayTab[i] + j]);
          console.log(res.data.list[dayTab[i] + j].main["preasure"]); //ciśnienie,
          console.log(res.data.list[dayTab[i] + j].main["humidity"]); //wilgotność
          console.log(res.data.list[dayTab[i] + j].main["temp"]); //temperatura
          console.log(res.data.list[dayTab[i] + j].weather["id"]); //status pogody - ikona
          console.log(res.data.list[dayTab[i] + j].dt_txt); //status pogody - ikona
          let date = res.data.list[dayTab[i] + j].dt_txt;
          date=date.slice(0,10);
          let tempMor = res.data.list[dayTab[i] + j].main["temp"];
          let tempEvn = res.data.list[dayTab[i+1] + j].main["temp"];
          return (
            <div id="exampleCity">
              <div key={j - 7} className="App-panel2">
                <h3>{date}</h3>
                <div className="App-degree">
                  <p>tempMor&deg;C / tempEvn&deg;C</p>
                </div>
                <div>
                  {/* <img src={sun} alt="sun" className="App-icon" /> */}
                </div>
              </div>
            </div>
          );
        }

      }

      // console.log(res.data.list[afternoon]);
      // console.log(res.data.list[evening]);
      // console.log(res.data.list[night]);
      //console.log(res.data.main['temp']);
    })
    .catch((error) => {
      console.error("Błąd pobierania prognozy pogody:", error);
    });

     return (
       <div id="exampleCity">
         <div className="App-panel2">
           <h3>XD</h3>
           <div className="App-degree">
             <p>tempMor&deg;C / tempEvn&deg;C</p>
           </div>
           <div>{/* <img src={sun} alt="sun" className="App-icon" /> */}</div>
         </div>
       </div>
     );
}

export default WeatherAPI;
