import React, { useRef, useEffect, useState } from "react";
import { Chart } from "chart.js";
import axios from "axios";

const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";// Twój klucz API WeatherAPI
const API_URL = "https://api.weatherapi.com/v1/forecast.json";

const WeatherHourlyChart = ({ city }) => {
  const [hourlyData, setHourlyData] = useState(null);

  useEffect(() => {
    async function fetchWeatherData() {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/forecast?q=Gorz%C3%B3w%20WIelkopolski&units=metric&appid=27e27f3b62720afb770645ec7ae89aa7`
        );
        const data = response.data;
        console.log(data);

        // Przetwarzanie danych godzinowej prognozy pogody
        const hourlyForecast = data.list.map(
          (item) => ({
            time: item.time, // Czas
            temperature: item.temp_c, // Temperatura w stopniach Celsiusza
            // Dodaj inne niezbędne informacje
          })
        );

        setHourlyData(hourlyForecast);
      } catch (error) {
        console.error("Błąd pobierania prognozy pogody:", error);
      }
    }

    fetchWeatherData();
  }, [city]);

  useEffect(() => {
    if (hourlyData) {
      // Generowanie wykresu po otrzymaniu danych godzinowej prognozy pogody
      generateChart();
    }
  }, [hourlyData]);

  const generateChart = () => {
    const labels = hourlyData.map((item) => item.time);
    const temperatures = hourlyData.map((item) => item.temperature);

    const ctx = document.getElementById("weatherChart").getContext("2d");
    new Chart(ctx, {
      type: "line",
      data: {
        labels: labels,
        datasets: [
          {
            label: "Temperatura (°C)",
            data: temperatures,
            borderColor: "blue",
            backgroundColor: "rgba(0, 0, 255, 0.2)",
            fill: true,
          },
        ],
      },
      options: {
        responsive: true,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  return <canvas id="weatherChart" width="400" height="200" />;
};

export default WeatherHourlyChart;
