import logo from "../img/logo2.png";
import sun from "../img/weather-icons/sun.png";
import "../App.css";
import "../styles/WeatherForcast-style.css";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import WeatherAPI from "../scripts/WeatherAPI";
import Layout from "../layout/layout";
import WeatherAPIConf from "../scripts/WeatherAPIConf";

function WeatherForcast({ data }) {
  WeatherAPIConf.API_CITY = data.cityName || "Gdańsk";
  WeatherAPIConf.API_UNITS = data.units === false ? "&units=metric" : "&units=imperial";  

  
  let dateCurrent = new Date();
  return (
    <Layout>
      <section>
        <div className="App-panel">
          <div className="date">
            {dateCurrent.toString().split(" ")[0] + " "}
            {dateCurrent.toLocaleDateString("pl-PL")}
          </div>
          <div className="content">
            <h2>{WeatherAPIConf.API_CITY}</h2>
            <div className="weatherData">
              <div>{}</div>
            </div>
          </div>
        </div>

        <div id="exampleCity">
          <h2>Search the wather</h2>
        </div>
      </section>
    </Layout>
  );
}

export default WeatherForcast;
