import React, { useEffect, useState } from "react";
import Axios from 'axios';
function WeatherAPI(cityName, units) {
  const [response, setResponse] = useState([]);

  

  const API_LINK = "https://api.openweathermap.org/data/2.5/forecast?q=";
  const API_CITY = cityName || "Gdańsk";
  const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";
  const API_UNITS =
    units.checked === false ? "&units=metric" : "&units=imperial";
  const API_LANG = "&lang=pl";

  // const API_URL = API_LINK + API_CITY + API_UNITS + API_KEY;
  const API_URL =
    "https://api.openweathermap.org/data/2.5/forecast?q=Gorz%C3%B3w%20Wielkopolski&units=imperial&lang=pl&appid=27e27f3b62720afb770645ec7ae89aa7";

  async function getData() {
    const response = await fetch(API_URL);
    const data = await response.json();
    setResponse(data);
    console.log(data);
  }
  getData();

  fetch(API_URL, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((res) => {
      if (res.ok) {
        console.log(res);
      } else {
        console.log("Mamy problem !");
      }
    })
    .catch((error) => {
      console.error(error);
    });
}

export default WeatherAPI;
