import React from "react";
import axios from "axios";

function WeatherAPI(cityName, units) {
  const API_LINK = "https://api.openweathermap.org/data/2.5/forecast?q=";
  const API_CITY = cityName || "Gdańsk";
  const API_KEY = "&appid=27e27f3b62720afb770645ec7ae89aa7";
  const API_UNITS = units === false ? "&units=metric" : "&units=imperial";
  const API_LANG = "&lang=pl";

  const API_URL = API_LINK + API_CITY + API_UNITS + API_KEY;

  axios
    .get(API_URL)
    .then((res) => {
      let morning = 3;
      let afternoon = 5;
      let evening = 7;
      let night = 8;
      let dayTab = [morning, afternoon, evening, night];

      for (let i = 0; i < dayTab.length; i++) {
        for (let j = 0; j <= 40; j+=8) {
          console.log(res.data);
          console.log(j);
          console.log(res.data.list[dayTab[i]+j]);
          console.log(res.data.list[dayTab[i]+j].main["preasure"]); //ciśnienie,
          console.log(res.data.list[dayTab[i]+j].main["humidity"]); //wilgotność
          console.log(res.data.list[dayTab[i]+j].main["temp"]); //temperatura
          console.log(res.data.list[dayTab[i]+j].weather["id"]); //status pogody - ikona
        }
      }

      // console.log(res.data.list[afternoon]);
      // console.log(res.data.list[evening]);
      // console.log(res.data.list[night]);
      //console.log(res.data.main['temp']);
    })
    .catch((error) => {
      console.error("Błąd pobierania prognozy pogody:", error);
    });
}

export default WeatherAPI;
