import logo from "../img/logo2.png";
import sun from "../img/weather-icons/sun.png";
import "../App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import WeatherAPI from "../scripts/WeatherAPI";
import Layout from "../layout/layout";
import WeatherAPIConf from "../scripts/WeatherAPIConf";
import iconThunderstorm from "../img/weather-icons/thunderstorm.png";
import iconDizzle from "../img/weather-icons/drizzle.png";
import iconRain from "../img/weather-icons/rain.png";
import iconIce from "../img/weather-icons/ice.png";
import iconUnknown from "../img/weather-icons/unknown.png";
import iconFog from "../img/weather-icons/fog.png";
import iconSun from "../img/weather-icons/sun.png";
import iconCloud from "../img/weather-icons/cloud.png";

function Main({ submitHandler }) {
  const [cityName, setCityName] = useState("");
  const [units, setUnits] = useState(false);
  const [temperature1, setTemperature1] = useState(null);
  const [status1, setStatus1] = useState(null);
  const [exampleCityName1, setExampleCityName1] = useState(null);
  const [temperature2, setTemperature2] = useState(null);
  const [status2, setStatus2] = useState(null);
  const [exampleCityName2, setExampleCityName2] = useState(null);
  const [temperature3, setTemperature3] = useState(null);
  const [status3, setStatus3] = useState(null);
  const [exampleCityName3, setExampleCityName3] = useState(null);
  const [temperature4, setTemperature4] = useState(null);
  const [statu4, setStatus4] = useState(null);
  const [exampleCityName4, setExampleCityName4] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(units);
    //
    //console.log(WeatherAPI(cityName, units));
    submitHandler({ cityName, units });
    navigate("/weatherforcast");
  };

  // const searchCityWeather = (tab) => {
  //   //wykonuje zapytanie do api w funkcji
  //   for (let i = 0; i < tab.lenght; i++) {
  //     return <div>{WeatherAPI(tab[i], units)}</div>;
  //   }
  // };

  let getWeather = (city1, city2, units = false) => {
    WeatherAPIConf.API_FORMAT = "weather";
    WeatherAPIConf.API_CITY = city1;
    WeatherAPIConf.API_UNITS =
      units === false ? "&units=metric" : "&units=imperial";

    let API_URL =
      WeatherAPIConf.API_LINK +
      WeatherAPIConf.API_FORMAT +
      WeatherAPIConf.API_CITYQ +
      WeatherAPIConf.API_CITY +
      WeatherAPIConf.API_UNITS +
      WeatherAPIConf.API_KEY;

    axios
      .get(API_URL)
      .then((res) => {
        let temperature1 = res.data.main["temp"];
        let status1 = res.data.weather[0]["id"];
        setTemperature1(Math.round(temperature1));
        setStatus1(status1);
        console.log(status1);
        setExampleCityName1(city1);
      })
      .catch((error) => {
        //console.error("Błąd pobierania prognozy pogody:", error);
      });

    API_URL =
      WeatherAPIConf.API_LINK +
      WeatherAPIConf.API_FORMAT +
      WeatherAPIConf.API_CITYQ +
      WeatherAPIConf.API_CITY +
      WeatherAPIConf.API_UNITS +
      WeatherAPIConf.API_KEY;
    WeatherAPIConf.API_CITY = city1;

    axios
      .get(API_URL)
      .then((res) => {
        let temperature2 = res.data.main["temp"];
        let status2 = res.data.weather[0]["id"];
        setTemperature2(Math.round(temperature2));
        setStatus2(status2);
        console.log(A);
        setExampleCityName2(city2);
      })
      .catch((error) => {
        //console.error("Błąd pobierania prognozy pogody:", error);
      });

    // axios
    //   .get(API_URL)
    //   .then((res) => {
    //     let temperature = res.data.main["temp"];
    //     let status = res.data.weather[0]["id"];
    //     setTemperature(Math.round(temperature3));
    //     setStatus(status3);
    //     setExampleCityName(city3);
    //   })
    //   .catch((error) => {
    //     console.error("Błąd pobierania prognozy pogody:", error);
    //   });

    // axios
    //   .get(API_URL)
    //   .then((res) => {
    //     let temperature = res.data.main["temp"];
    //     let status = res.data.weather[0]["id"];
    //     setTemperature(Math.round(temperature4));
    //     setStatus(status4);
    //     setExampleCityName(city4);
    //   })
    //   .catch((error) => {
    //     console.error("Błąd pobierania prognozy pogody:", error);
    //   });
  };

  const getIcon = (statusCode) => {
    if (statusCode >= 200 && statusCode < 300) {
      return iconThunderstorm;
    } else if (statusCode >= 300 && statusCode < 400) {
      return iconDizzle;
    } else if (statusCode >= 500 && statusCode < 600) {
      return iconRain;
    } else if (statusCode >= 600 && statusCode < 700) {
      return iconIce;
    } else if (statusCode >= 700 && statusCode < 800) {
      return iconFog;
    } else if (statusCode == 800) {
      return iconSun;
    } else if (statusCode > 800 && statusCode < 900) {
      return iconCloud;
    }
  };

  let exampleCityWeather = (city, city2, units) => {
    let tempUnit = units === false ? "°C" : "°F";
    getWeather(city, city2, units);

    return (
      <div className="App-panel2">
        <h3>{exampleCityName1}</h3>
        <h3>{exampleCityName2}</h3>
        <div className="weatherData2">
          <p>
            {temperature1}
            {temperature2}
            {tempUnit}
          </p>
        </div>
        <div className="icon2">
          <img src={getIcon(status1)} alt="sun" className="App-icon" />
          <img src={getIcon(status2)} alt="sun" className="App-icon" />
        </div>
      </div>

      
    );
    console.log("XD");
  };

  return (
    <Layout>
      <section>
        <div id="searchPanel">
          <div className="App-panel">
            <h2>Search the wather</h2>
            <form method="GET" onSubmit={handleSubmit}>
              <div class="button r" id="button-3">
                <input
                  type="checkbox"
                  class="checkbox units"
                  name="units"
                  checked={units}
                  onChange={(event) => setUnits(event.target.checked)}
                />
                <div class="knobs"></div>
                <div class="layer"></div>
              </div>
              <input
                type="text"
                name="city"
                id="city"
                placeholder="city"
                value={cityName}
                onChange={(event) => setCityName(event.target.value)}
              />
              <input type="submit" value="Search" />
            </form>
          </div>
        </div>
        <div id="exampleCity">
          {exampleCityWeather("Warszawa", "London", units)}
        </div>
      </section>
    </Layout>
  );
}

export default Main;
