import logo from "../img/logo2.png";
import sun from "../img/weather-icons/sun.png";
import "../App.css";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import WeatherAPI from "../scripts/WeatherAPI";

function Main() {
  const [cityName, setCityName] = useState("");
  const [units, setUnits] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    WeatherAPI(cityName, units);
  };

  const exampleCity = (tab) => {
    //wykonuje zapytanie do api w funkcji
    return (
      <div id="exampleCity">
        {tab.map((item, index) => (
          <div key={index} className="App-panel2">
            <h3>{item}</h3>
            <div className="App-degree">
              <p>25&deg;C</p>
            </div>
            <div>
              <img src={sun} alt="sun" className="App-icon" />
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <section>
      <div id="searchPanel">
        <div className="App-panel">
          <h2>Search the wather</h2>
          <form method="GET" onSubmit={handleSubmit}>
            <div class="button r" id="button-3">
              <input
                type="checkbox"
                class="checkbox units"
                name="units"
                value={units}
                onChange={(event) => setUnits(event.target.value)}
              />
              <div class="knobs"></div>
              <div class="layer"></div>
            </div>
            <input
              type="text"
              name="city"
              id="city"
              placeholder="city"
              value={cityName}
              onChange={(event) => setCityName(event.target.value)}
            />
            <input type="submit" value="Search" />
          </form>
        </div>
      </div>
      {exampleCity(["Warszawa", "Nowy York", "Londyn", "Paryż"])}
    </section>
  );
}

export default Main;
